// FUNZIONE PER COPIARE LA DIRECTORY
function copydir ($source, $dest) {

     if (is_file($source)) {
         return copy($source, $dest);
     }

     if (!is_dir($dest)) {
         mkdir($dest);
     }

     $dir = dir($source);
     while (false !== $entry = $dir->read()) {

         if ($entry == '.' || $entry == '..') {
             continue;
         }

         if ($dest !== "$source/$entry") {
             copydir("$source/$entry", "$dest/$entry");
         }
     }

     $dir->close();
     return true;
}
