       foreach ($data as $id => $values) {
           if (isset($values['showemail']))
               $data[$id]['showemail'] = 1;
           else
               $data[$id]['showemail'] = 0;
       }

       foreach ($data as $id => $values) {
           query("UPDATE users SET " . implode_with_keys(",", $values, "'") . " WHERE user_id='$id'");
       }
       print "<h3>All users updated</h3>";
