   {
      mysql_query("BEGIN;");
      $items = Cart::cart_items($cart['id']);
      $coupons = Cart::cart_coupons($cartid);
      $sum = 0;
      foreach ($items as $item)
      {
	 $sum += $item['price'];
	 $query = sprintf("INSERT into `own` (`id`, `user_id`, `picture_id`) VALUES (NULL, '%d', '%d');",
